mapper.select:
select qnano, userid, qnasubject, qnacontent, hit,
		case when date_format(regtime, '%Y%m%d') = date_format(now(), '%Y%m%d')
		then date_format(regtime, '%H:%i:%s')
		else date_format(regtime, '%y.%m.%d')
		end regtime
from qna


		where qnasubject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by qnano desc
limit #{start}, #{spp}

select count(qnano) from qna
where qnasubject like concat('%', #{word}, '%')
where ${key} = #{word}

select qnano, userid, qnasubject, qnacontent, hit, regtime
from qna
where qnano = #{qnano}

mapper.update:
update qna
set hit = hit + 1
where qnano = #{qnano}

update qna
set qnasubject = #{qnasubject}, qnacontent = #{qnacontent}
where qnano = #{qnano}
